// LC 680 Valid Palindrome II
// EASY
// https://leetcode.com/problems/valid-palindrome-ii/
// 
// Given a non-empty string s, you may delete at most one character. 
// Judge whether you can make it a palindrome.
// 
// Note:
// The string will only contain lowercase characters a - z. The maximum length 
// of the string is 50000.
//
// EXAMPLE 1:
// Input: "aba"
// Output: True
//
// EXAMPLE 2:
// Input: "abca"
// Output: True
// Explanation: You could delete the character 'c'.



// *****************************************************************************
// SOLUTION V1- Naive solution- 30MIN
// TIME COMPLEXITY:  O(N^2),    N = str length  
// SPACE COMPLEXITY: O(N)

// "abcda"    =>  false
var v1ValidPalindromeII = function (str) {
  
  // create array of split chars of str
  let strArr = str.split('');
  // strArr = [ 'a', 'b', 'c', 'd', 'a' ]

  // loop through each char in str
  for (let i = 0; i < str.length; i++) {

    // remove current char from str
    strArr.splice(i, 1);
    // strArr = [ 'b', 'c', 'd', 'a' ]

    // pass updated str into helper function to check for palindrome
    if (validPalindromeI(strArr)) return true;

    // "put back" the char we took out
    strArr = str.split("");
  }
  
  return false;
};


// HELPER
// TIME COMPLEXITY:  O(1/2 * N) => O(N) using two pointers
function validPalindromeI(arr) {
  let i = 0;
  let j = arr.length - 1;

  let punctuation = new Set([',', ':', '.', ';', '?', "'", '"', '!']);
  str = str.toLowerCase();
  let newStr = '';
  for (let i = 0; i < str.length; i++) {
    let char = str[i];
    if (!punctuation.has(char)) newStr += char;
  }

  while (i < j) {
    let char = arr[i];
    let opposite = arr[j];
    if (char !== opposite) return false;
    i++;
    j--;
  }

  return true;
}
